mapper.update:
update board
brd_cate_no=#{brdCateNo}, brd_writer_no=#{brdWriterNo}, brd_title=#{brdTitle}, brd_content=#{brdContent},
where brd_no=#{brdNo} and brd_writer_no=#{brdWriterNo}

mapper.select:
SELECT v.* FROM ( SELECT /*+ index_desc(BOARD PK_BOARD) */ ROWNUM AS rnum, a.* FROM ( SELECT b.brd_no, b.brd_writer_no, b.brd_title, b.brd_content, b.brd_views, b.brd_created_at, i.img_path, c.brd_cate_name, d.address_no, d.address_si, d.address_gu, d.address_dong FROM BOARD b left outer join ADDRESS d on b.brd_writer_no = d.mem_no left outer join BOARD_IMAGE i on b.brd_no = i.brd_no left outer join BOARD_CATEGORY c on b.brd_cate_no = c.brd_cate_no where d.address_si = #{addressSi} and d.address_gu = #{addressGu} and d.address_dong = #{addressDong} and (i.img_no = 1 or i.img_no is null)
and (b.brd_title LIKE '%'|| #{keyword} ||'%' or b.brd_content Like '%'|| #{keyword} ||'%')
and b.brd_cate_no=#{brdCateNo}
and b.brd_writer_no = #{brdWriterNo}
ORDER BY b.brd_no DESC ) a WHERE ROWNUM <= #{end} ) v WHERE v.rnum >= #{start}
