mapper.update:
update PRODUCT
pdt_cate_no=#{pdtCateNo}, pdt_address_no=#{pdtAddressNo}, pdt_name=#{pdtName}, pdt_desc=#{pdtDesc}, pdt_price=#{pdtPrice}, pdt_status=#{pdtStatus},
where pdt_no=#{pdtNo}

mapper.select:
SELECT b.pdt_no, b.pdt_address_no, b.pdt_name, b.pdt_status, b.pdt_price, b.pdt_views, b.pdt_created_at FROM ( SELECT /*+ index_desc(PRODUCT PK_PRODUCT) */ ROWNUM AS rnum, a.pdt_no, a.pdt_address_no, a.pdt_name, a.pdt_status, a.pdt_price, a.pdt_views, a.pdt_created_at FROM ( SELECT pdt_no, pdt_address_no, pdt_name, pdt_status, pdt_price, pdt_views, pdt_created_at FROM product p left join address d on p.pdt_address_no = d.address_no where d.address_si = #{addressSi} and d.address_gu = #{addressGu} and d.address_dong = #{addressDong}
and pdt_name LIKE '%'|| #{pdtName} ||'%' or pdt_desc Like '%'|| #{pdtDesc} ||'%'
and pdt_cate_no LIKE '%'|| #{pdtCateNo} ||'%'
ORDER BY pdt_no DESC ) a WHERE ROWNUM <= #{end} ) b WHERE b.rnum >= #{start}
